mapper.insert:
insert into ass_level ( person_name, person_code, person_sex, person_phone, person_remark, level_id, department1_id, department2_id, person_remark1, person_remark2, person_remark3, person_remark4, logic_delete, created_by, updated_by, created_time, updated_time, conf_region_id, conf_dept_id
)
values (
#{person_name}, #{person_code}, #{person_sex}, #{person_phone}, #{person_remark}, #{level_id}, #{department1_id}, #{department2_id}, #{person_remark1}, #{person_remark2}, #{person_remark3}, #{person_remark4}, #{logic_delete}, #{created_by}, #{updated_by}, #{created_time}, #{updated_time}, #{conf_region_id}, #{conf_dept_id}
)

mapper.update:
update ass_level
logic_delete = b'1', updated_by = #{updated_by}, updated_time = #{updated_time},
where level_id = #{level_id,jdbcType=BIGINT}
